    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        int nb;
        
        if(this.celsius){
            
            
            try{
                nb = Integer.parseInt(jTextField1.getText());
            }
            catch(NumberFormatException e){
                jTextField1.setText("");
                return;
            }
            
            
            int d = Math.round(nb * 2.74f);
            jTextField2.setText(String.valueOf(d));
            
        }
        else{
            
            try{
                nb = Integer.parseInt(jTextField2.getText());
            }
            catch(NumberFormatException e){
                jTextField2.setText("");
                return;
            }
            
            int c = Math.round(Integer.parseInt(jTextField2.getText()) / 2.74f);
            jTextField1.setText(String.valueOf(c));
            
        }
            


    }//GEN-LAST:event_jButton1ActionPerformed

    private void jCheckBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBox1ActionPerformed
        // TODO add your handling code here:
        jCheckBox2.setSelected(false);
        jCheckBox1.setSelected(true);
        this.celsius = true;
    }//GEN-LAST:event_jCheckBox1ActionPerformed

    private void jCheckBox2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCheckBox2ActionPerformed
        // TODO add your handling code here:
        jCheckBox1.setSelected(false);
        jCheckBox2.setSelected(true);
        this.celsius = false;
    }//GEN-LAST:event_jCheckBox2ActionPerformed

   


